mapper.select:
	SELECT user_no as userNo, username, birth, email, password, regdate
   FROM users
   WHERE email=#{value}

SELECT user_no as userNo, username, birth, email, password, regdate, role
FROM users
WHERE email=#{email} AND password=#{password}

mapper.update:
UPDATE users
SET username=#{username}, birth=#{birth}, email=#{email}, password=#{password}, regdate=#{regdate}
WHERE user_no=#{userNo}
